      // `amount` token getting minted here & equal amount got locked in RootChainManager\n        _totalSupply = _totalSupply.add(amount);\n        _balances[user] = _balances[user].add(amount);\n        \n        emit Transfer(address(0), user, amount);\n    }\n\n    function withdraw(uint256 amount) external {\n        _balances[msg.sender] = _balances[msg.sender].sub(amount, "ERC20: burn amount exceeds balance");\n        _totalSupply = _totalSupply.sub(amount);\n        \n        emit Transfer(msg.sender, address(0), amount);\n    }\n\n}\n')),(0,r.kt)("p",null,"This updated implementation can be used for mapping."),(0,r.kt)("p",null,"Steps :"),(0,r.kt)("ol",null,(0,r.kt)("li",{parentName:"ol"},"Deploy root token on root chain i.e. {Goerli, Ethereum Mainnet}"),(0,r.kt)("li",{parentName:"ol"},"Ensure your child token has the ",(0,r.kt)("inlineCode",{parentName:"li"},"deposit")," & ",(0,r.kt)("inlineCode",{parentName:"li"},"withdraw")," functions."),(0,r.kt)("li",{parentName:"ol"},"Deploy the child token on child chain i.e. {Polygon Mumbai, Polygon Mainnet}"),(0,r.kt)("li",{parentName:"ol"},"Submit a mapping request, to be resolved by team.")),(0,r.kt)("h3",{id:"request-submission"},"Request Submission"),(0,r.kt)("p",null,"Please go use ",(0,r.kt)("a",{parentName:"p",href:"/docs/develop/ethereum-polygon/submit-mapping-request"},"this link")," to submit a mapping request."))}m.isMDXComponent=!0}}]);